mapper.insert:
		INSERT INTO SPABOARD.TB_NETFLIX (		
   				BOARD_NO
   				, CONTENTS_TITLE
   				, GRADES
   				, CONTENTS_URL
   		)
    	VALUES (
	           #{idx}
	           , #{contents_title}
	           , #{grades}
	           , #{contents_url} )

mapper.update:
    		UPDATE SPABOARD.TB_BOARD
    		SET     TITLE            =  #{title}
	    		 ,  CONTENTS         =  #{contents}
    			 ,  UPDDATE          =  now()
    		WHERE IDX = #{idx}

    		UPDATE SPABOARD.TB_NETFLIX
    		SET     CONTENTS_TITLE   =  #{contents_title}
    			 ,  CONTENTS_URL     =  #{contents_url}
	    		 ,  GRADES           =  #{grades}
    			 ,  UPDDATE          =  now()
    		WHERE BOARD_NO = #{idx}

	UPDATE SPABOARD.TB_BOARD
	SET    COUNT = COUNT + 1
    WHERE IDX = #{idx}

mapper.insert:
		INSERT INTO SPABOARD.TB_FILE (	
   				BOARD_NO 
   				, FILE_NAME
   				, ORIGIN_NAME
   				, FILE_PATH
   				, FILE_TYPE
   				, REGDATE
   				, UPDDATE
   		)
    	VALUES (
	    	   #{board_no} 
	           , #{file_name}
	           , #{origin_name}
	           , #{file_path}
	           , #{file_type} 
	           , now()
	           , now()	)
